mapper.update:
update class1
set class1_name = #{class1Name,jdbcType=VARCHAR},
  class1_num = #{class1Num,jdbcType=INTEGER},
  remark = #{remark,jdbcType=VARCHAR}
where class1_id = #{class1Id,jdbcType=INTEGER}

mapper.select:
select class1_id, class1_name, class1_num, remark
from class1
where class1_id = #{class1Id,jdbcType=INTEGER}
