mapper.update:
update class2
set class2_name = #{class2Name,jdbcType=VARCHAR},
class1_id = #{class1Id,jdbcType=INTEGER},
remark = #{remark,jdbcType=VARCHAR}
where class2_id = #{class2Id,jdbcType=INTEGER}

mapper.select:
select class2_id, class2_name, class1_id, remark
from class2
where class2_id = #{class2Id,jdbcType=INTEGER}
