mapper.insert:
insert into order_detail (order_detail_id, order_id, good_id,
good_name, transaction_price, transaction_count
)
values (#{orderDetailId,jdbcType=INTEGER}, #{orderId,jdbcType=INTEGER},
#{goodId,jdbcType=INTEGER},
#{goodName,jdbcType=VARCHAR}, #{transactionPrice,jdbcType=DOUBLE},
#{transactionCount,jdbcType=INTEGER}
)

mapper.select:
select order_detail_id, order_id, good_id, good_name, transaction_price,
transaction_count
from order_detail where order_id = #{orderId,jdbcType=INTEGER}
